// eslint-disable-next-line @typescript-eslint/no-explicit-any
function InteractionTool({ name, children, extendedClass }: { name: string, children: React.ReactElement<any>, extendedClass?: string }) {
	const clonedIcon = React.cloneElement(children, {
		className: `
			h-[25px] w-[25px]
			md:h-[20px] md:w-[20px]
			min-[2000px]:h-[30px] min-[2000px]:w-[30px]
		`
	})

	return (
		<div className={`${name} md:border-gray-300 md:border p-2 sm:p-3 md:p-3 rounded-full hover:bg-gray-100 ${extendedClass}`}>
			{ clonedIcon }
		</div>
	)
}

